mapper.insert:
insert into patient (
  	patient_name, patient_belong, patient_phone, patient_sex, patient_create_time, patient_last_edit_time,
  	patient_describe, patient_src
  ) values (
    #{patientName}, #{patientBelong}, #{patientPhone}, #{patientSex}, #{patientCreateTime}, #{patientLastEditTime},
	#{patientDescribe}, #{patientSrc}
  )

mapper.select:
select * from patient where
    patient_belong = #{departmentId}
	and (patient_id = #{queryString}
	or patient_name like '%${queryString}%'
	or patient_phone = #{queryString})
limit #{rowIndex}, #{pageSize}

select count(*)
from patient where
patient_belong = #{departmentId}
and (patient_id = #{queryString}
or patient_name like '%${queryString}%'
or patient_phone = #{queryString})
